import React from "react";

const dummyRecipe = {
  name: "Rode pasta",
  quantityPerson: 4,
  duration: 45,
  labelTypeDish: "meat",
  kitchen: "italian",
  healthy: false,
  image:
    "https://static.ah.nl/static/recepten/img_RAM_PRD137625_1224x900_JPG.jpg",
  ingredients: [
    {
      name: "paprika",
      quantity: "1",
      quantityType: "piece",
      ingredientType: "vegetable",
    },
    {
      name: "courgette",
      quantity: "1",
      quantityType: "piece",
      ingredientType: "vegetable",
    },
    {
      name: "ui",
      quantity: "1",
      quantityType: "piece",
      ingredientType: "vegetable",
    },
    {
      name: "wortels",
      quantity: "3",
      quantityType: "piece",
      ingredientType: "vegetable",
    },
    {
      name: "tomatenpuree",
      quantity: "70",
      quantityType: "gram",
      ingredientType: "sauce",
    },
    {
      name: "tomatenblokjes",
      quantity: "400",
      quantityType: "gram",
      ingredientType: "sauce",
    },
    {
      name: "pasta",
      quantity: "500",
      quantityType: "gram",
      ingredientType: "doughware",
    },
    {
      name: "geraspte kaas",
      quantity: "200",
      quantityType: "gram",
      ingredientType: "dairy",
    },
    {
      name: "oregano",
      quantity: "1",
      quantityType: "teaspoon",
      ingredientType: "spices",
    },
    {
      name: "gehakt",
      quantity: "500",
      quantityType: "gram",
      ingredientType: "meat",
    },
  ],
};

const {
  name,
  quantityPerson,
  duration,
  labelTypeDish,
  kitchen,
  healthy,
  image,
  ingredients,
} = dummyRecipe;

const convertQuantity = (quantity) => {
  switch (quantity) {
    case "piece":
      return "";
    case "gram":
      return "gram";
    case "teaspoon":
      return "theelepel";
    default:
      return "";
  }
};

const convertLabelTypeDish = (label) => {
  switch (label) {
    case "meat":
      return "vleesgerecht";
    case "fish":
      return "visgerecht";
    case "vegetarian":
      return "vegetarisch gerecht";
    case "vegan":
      return "veganistisch gerecht";
    default:
      return "";
  }
};

const RecipeOverview = () => {
  return (
    <div className="recipeOverview">
      <header className="recipeOverview__header">
        <img
          src={image}
          alt={name}
          className="recipeOverview__header--image"
        />
        <h1 className="recipeOverview__header--title">{name}</h1>
      </header>
      <section className="recipeOverview__labels">
        <div className="label dish-type">{convertLabelTypeDish(labelTypeDish)}</div>
        <div className="label kitchen">{kitchen}</div>
      </section>
      <section className="recipeOverview__ingredients">
        <h1 className="sectionTitle">IngrediÃ«nten</h1>
        <ul>
          {ingredients.map((item, i) => (
            <li key={i}>
              <span className="quantity">
                {item.quantity} {convertQuantity(item.quantityType)}
              </span>
              <span className="name">{item.name}</span>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
};

export default RecipeOverview;
