import React from "react";

const AlterRecipe = ({ recipe }) => {
  console.log(recipe);
  const {
    name,
    quantityPerson,
    duration,
    labelTypeDish,
    kitchen,
    healthy,
    image,
    ingredients,
    preperationSteps,
  } = recipe;

  const dishTypes = [
    {
      shortName: 'meat',
      longName: 'vleesgerecht',
    },
    {
      shortName: 'fish',
      longName: 'visgerecht',
    },
    {
      shortName: 'vegetarian',
      longName: 'vegetarisch gerecht',
    },
    {
      shortName: 'vegan',
      longName: 'veganistisch gerecht',
    },
  ];

  return (
    <div className="alterRecipe">
      <div className="alterRecipe__header">
        <h1>Recept aanpassen</h1>
        <div className="alterRecipe__header--buttons">
          <button>Opslaan</button>
          <button>Annuleren</button>
        </div>
      </div>
      <div className="alterRecipe__content">
        <div className="alterRecipe__content--item">
          <label htmlFor="name">Titel</label>
          <input type="text" defaultValue={name} id="name" />
        </div>
        <div className="alterRecipe__content--item">
          <label htmlFor="quantityPerson">Hoeveelheid personen</label>
          <input type="number" defaultValue={quantityPerson} id="quantityPerson" />
        </div>
        <div className="alterRecipe__content--item">
          <label htmlFor="duration">Bereidingstijd in minuten</label>
          <input type="number" defaultValue={duration} id="duration" />
        </div>
        <div className="alterRecipe__content--item">
          <label htmlFor="labelTypeDish">Soort gerecht</label>
          <select>
            {dishTypes.map((type, index) => {
              if (type.shortName === labelTypeDish) {
                return <option key={index} selected>{type.longName}</option>
              }
              return <option key={index} >{type.longName}</option>
            })}
          </select>
        </div>
        <div className="alterRecipe__content--item">
          <label htmlFor="kitchen">Bereidingstijd in minuten</label>
          <input type="number" defaultValue={kitchen} id="kitchen" />
        </div>
        <div className="alterRecipe__content--item">
          <label htmlFor="healthy">Bereidingstijd in minuten</label>
          <input type="number" defaultValue={healthy} id="healthy" />
        </div>
        <div className="alterRecipe__content--item">
          <label htmlFor="image">Bereidingstijd in minuten</label>
          <input type="number" defaultValue={image} id="image" />
        </div>
      </div>
    </div>
  );
};

export default AlterRecipe;
