import React, { useState } from "react";
import { APIHandler } from "config/C4";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus, faMinus } from "@fortawesome/free-solid-svg-icons";

const addRecipe = async () => {
  APIHandler.addRecipe();
};

const dishTypes = [
  {
    shortName: "meat",
    longName: "Vleesgerecht",
  },
  {
    shortName: "fish",
    longName: "Visgerecht",
  },
  {
    shortName: "vegetarian",
    longName: "Vegetarisch gerecht",
  },
  {
    shortName: "vegan",
    longName: "Veganistisch gerecht",
  },
];

const kitchenTypes = [
  {
    shortName: "french",
    longName: "Frans",
  },
  {
    shortName: "italian",
    longName: "Italiaans",
  },
  {
    shortName: "thai",
    longName: "Thais",
  },
  {
    shortName: "chinese",
    longName: "Chinees",
  },
  {
    shortName: "mexican",
    longName: "Mexicaans",
  },
];

const quantityTypes = [
  {
    shortName: "piece",
    longName: "stuks",
  },
  {
    shortName: "gram",
    longName: "gram",
  },
  {
    shortName: "teaspoon",
    longName: "theelepel",
  },
];

const ingredientType = [
  {
    shortName: "vegetable",
    longName: "Groenten of Fruit",
  },
  {
    shortName: "sauce",
    longName: "Sauzen of Oliën",
  },
  {
    shortName: "spices",
    longName: "Kruiden",
  },
  {
    shortName: "meat",
    longName: "Vlees(vervangers)",
  },
  {
    shortName: "dairy",
    longName: "Zuivelproducten",
  },
  {
    shortName: "doughware",
    longName: "Deegwaren",
  },
  {
    shortName: "bread",
    longName: "Brood",
  },
  {
    shortName: "else",
    longName: "Overig",
  },
];

const AddRecipeView = () => {
  const [recipe, setRecipe] = useState({
    recipeName: '',
    quantityPerson: null,
    duration: null,
    labelTypeDish: '',
    kitchen: '',
    healthy: false,
    image: '',
    ingredients: [],
    preperationSteps: [],
  });
  const [ingredientCount, setIngredientCount] = useState(1);
  const [preperationStepsCount, setPreperationStepsCount] = useState(1);
  const placeholderImage =
    "https://ralfvanveen.com/wp-content/uploads/2021/06/Placeholder-_-Begrippenlijst.svg";

  const changeHandler = (e) => {
    const { id, value } = e.target;
    // const input = recipe;
    console.log(id, value);
  };

  const addIngredient = (e) => {
    console.log(e.target.value);
  };

  const adjustIngredientCount = (direction) => {
    if (direction === '+') {
      setIngredientCount(ingredientCount + 1);
    } else {
      setIngredientCount(ingredientCount - 1);
    }
  };

  const addPrepStep = (e, index) => {
    console.log(e, index);
  };

  const adjustStepCount = (direction) => {
    if (direction === '+') {
      setPreperationStepsCount(preperationStepsCount + 1);
    } else {
      setPreperationStepsCount(preperationStepsCount - 1);
    }
  };

  return (
    <div className="addRecipeView">
      <h1>Basis info recept</h1>
      <div className="row">
        <label htmlFor="name">Titel</label>
        <input type="text" id="recipeName" placeholder="Titel van het recept" onChange={changeHandler}/>
      </div>
      <div className="row">
        <label htmlFor="quantityPerson">Hoeveelheid personen</label>
        <input type="number" id="quantityPerson" placeholder="Hoeveelheid personen" onChange={changeHandler}/>
      </div>
      <div className="row">
        <label htmlFor="duration">Bereidingstijd in minuten</label>
        <input type="number" id="duration" placeholder="Bereidingstijd in minuten" onChange={changeHandler}/>
      </div>
      <div className="row">
        <label htmlFor="labelTypeDish">Soort gerecht</label>
        <select id="labelTypeDish" onChange={changeHandler}>
          <option disabled selected>
            Maak een keuze
          </option>
          {dishTypes.map((type, index) => {
            return (
              <option key={index} value={type.shortName}>
                {type.longName}
              </option>
            );
          })}
        </select>
      </div>
      <div className="row">
        <label htmlFor="kitchen">Afkomst van het gerecht</label>
        <select id="kitchen" onChange={changeHandler}>
          <option disabled selected>
            Maak een keuze
          </option>
          {kitchenTypes.map((type, index) => {
            return (
              <option key={index} value={type.shortName}>
                {type.longName}
              </option>
            );
          })}
        </select>
      </div>
      <div className="row">
        Is het een gezond gerecht?
        <div>
          <label>
            <input
              type="radio"
              id="healthy"
              name="healthy"
              value={true}
              onChange={changeHandler}
            />
            Ja
          </label>
          <label>
            <input
              type="radio"
              id="healthy"
              name="healthy"
              value={false}
              onChange={changeHandler}
            />
            Nee
          </label>
        </div>
      </div>
      <h1>Ingrediënten</h1>
      <div className="ingredients">
        <div className="row">
          <p>Hoeveelheid</p>
          <p>Eenheid</p>
          <p>Ingrediënt</p>
          <p>Type ingrediënt</p>
        </div>
        {[...Array(ingredientCount)].map((elem, index, { length }) => (
          <div key={index} className="row">
            <input type="number" id="quantity" onChange={addIngredient} />
            <select id="quantityType" onChange={addIngredient}>
              <option disabled selected>
                Maak een keuze
              </option>
              {quantityTypes.map((type, index) => {
                return (
                  <option key={index} value={type.shortName}>
                    {type.longName}
                  </option>
                );
              })}
            </select>
            <input type="text" id="name" onChange={addIngredient} />
            <select id="ingredientType" onChange={addIngredient}>
              <option disabled selected>
                Maak een keuze
              </option>
              {ingredientType.map((type, index) => {
                return (
                  <option key={index} value={type.shortName}>
                    {type.longName}
                  </option>
                );
              })}
            </select>
            {length - 1 === index && (
              <div className="adjustIngredientCount">
                <button onClick={() => adjustIngredientCount("+")}>
                  <FontAwesomeIcon icon={faPlus} />
                </button>
                <button onClick={() => adjustIngredientCount("-")} disabled={ingredientCount <= 1}>
                  <FontAwesomeIcon icon={faMinus} />
                </button>
              </div>
            )}
          </div>
        ))}
        <h1>Bereidingsstappen</h1>
        <div className="preperationSteps">
          {[...Array(preperationStepsCount)].map((elem, index, { length }) => (
            <div key={index} className="row">
              <div className="stepNumber">Stap nummer {index + 1}</div>
              <textarea onChange={(e) => addPrepStep(e, index)} />
              {length - 1 === index && (
                <div className="adjustIngredientCount">
                  <button onClick={() => adjustStepCount("+")}>
                    <FontAwesomeIcon icon={faPlus} />
                  </button>
                  <button onClick={() => adjustStepCount("-")} disabled={preperationStepsCount <= 1}>
                    <FontAwesomeIcon icon={faMinus} />
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AddRecipeView;
