import React, { useState } from 'react';
import { APIHandler } from 'config/C4';


const addRecipe = async () => {
  APIHandler.addRecipe();
}

const AddRecipeView = () => {
  const [recipe, setRecipe] = useState({});

  return (
    <div className="addRecipeView">
      <label htmlFor="name">Titel van het recept</label>
      <input type="text" name="name" id="name" placeholder="Titel van het recept" />
    </div>
  )
};

export default AddRecipeView;
