import React, { useState } from "react";
import { APIHandler } from "config/C4";

const addRecipe = async () => {
  APIHandler.addRecipe();
};

const dishTypes = [
  {
    shortName: "meat",
    longName: "Vleesgerecht",
  },
  {
    shortName: "fish",
    longName: "Visgerecht",
  },
  {
    shortName: "vegetarian",
    longName: "Vegetarisch gerecht",
  },
  {
    shortName: "vegan",
    longName: "Veganistisch gerecht",
  },
];

const kitchenTypes = [
  {
    shortName: "french",
    longName: "Frans",
  },
  {
    shortName: "italian",
    longName: "Italiaans",
  },
  {
    shortName: "thai",
    longName: "Thais",
  },
  {
    shortName: "chinese",
    longName: "Chinees",
  },
  {
    shortName: "mexican",
    longName: "Mexicaans",
  },
];

const quantityTypes = [
  {
    shortName: "piece",
    longName: "stuks",
  },
  {
    shortName: "gram",
    longName: "gram",
  },
  {
    shortName: "teaspoon",
    longName: "theelepel",
  },
];

const ingredientType = [
  {
    shortName: "vegetable",
    longName: "Groenten of Fruit",
  },
  {
    shortName: "sauce",
    longName: "Sauzen of Oliën",
  },
  {
    shortName: "spices",
    longName: "Kruiden",
  },
  {
    shortName: "meat",
    longName: "Vlees(vervangers)",
  },
  {
    shortName: "dairy",
    longName: "Zuivelproducten",
  },
  {
    shortName: "doughware",
    longName: "Deegwaren",
  },
  {
    shortName: "bread",
    longName: "Brood",
  },
  {
    shortName: "else",
    longName: "Overig",
  },
];

const AddRecipeView = () => {
  const [recipe, setRecipe] = useState({});
  const [ingredientCount, setIngredientCount] = useState(1);
  const placeholderImage =
    "https://ralfvanveen.com/wp-content/uploads/2021/06/Placeholder-_-Begrippenlijst.svg";

  const changeHandler = (e) => {
    console.log(e);
  };

  const addIngredient = (e) => {
    console.log(e);
  };

  return (
    <div className="addRecipeView">
      <h1>Basis info recept</h1>
      <label htmlFor="name">Titel</label>
      <input type="text" id="name" placeholder="Titel van het recept" />
      <label htmlFor="quantityPerson">Hoeveelheid personen</label>
      <input type="number" id="quantityPerson" />
      <label htmlFor="duration">Bereidingstijd in minuten</label>
      <input type="number" id="duration" />
      <label htmlFor="labelTypeDish">Soort gerecht</label>
      <select id="labelTypeDish" onChange={changeHandler}>
        {dishTypes.map((type, index) => {
          return (
            <option key={index} value={type.shortName}>
              {type.longName}
            </option>
          );
        })}
      </select>
      <label htmlFor="kitchen">Afkomst van het gerecht</label>
      <select id="kitchen" onChange={changeHandler}>
        {kitchenTypes.map((type, index) => {
          return (
            <option key={index} value={type.shortName}>
              {type.longName}
            </option>
          );
        })}
      </select>
      Is het een gezond gerecht?
      <input type="radio" id="healthy" value={true} onChange={changeHandler} />
      Ja
      <input type="radio" id="healthy" value={false} onChange={changeHandler} />
      Nee
      <label htmlFor="image">Afbeelding aanpassen</label>
      <img src={recipe.image || placeholderImage} alt="" width="200" />
      <input type="text" id="image" onChange={changeHandler} />
      <h1>Ingrediënten</h1>
      <div className="ingredients">
        <div className="row">
          <p>Hoeveelheid</p>
          <p>Eenheid</p>
          <p>Ingrediënt</p>
          <p>Type ingrediënt</p>
        </div>
        {[...Array(ingredientCount)].map((elem, index) => (
          <div key={index} className="row">
            <input type="number" id="quantity" onChange={addIngredient} />
            <select id="quantityType" onChange={addIngredient}>
              {quantityTypes.map((type, index) => {
                return (
                  <option key={index} value={type.shortName}>
                    {type.longName}
                  </option>
                );
              })}
            </select>
            <input type="text" id="name" onChange={addIngredient} />
            <select id="ingredientType" onChange={addIngredient}>
              {ingredientType.map((type, index) => {
                return (
                  <option key={index} value={type.shortName}>
                    {type.longName}
                  </option>
                );
              })}
            </select>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AddRecipeView;
