import React from "react";

const AlterRecipe = ({ recipe }) => {
  console.log(recipe);
  const {
    name,
    quantityPerson,
    duration,
    labelTypeDish,
    kitchen,
    healthy,
    image,
    ingredients,
    preperationSteps,
  } = recipe;

  const dishTypes = [
    {
      shortName: 'meat',
      longName: 'Vleesgerecht',
    },
    {
      shortName: 'fish',
      longName: 'Visgerecht',
    },
    {
      shortName: 'vegetarian',
      longName: 'Vegetarisch gerecht',
    },
    {
      shortName: 'vegan',
      longName: 'Veganistisch gerecht',
    },
  ];

  const kitchenTypes = [
    {
      shortName: 'italian',
      longName: 'Italiaans',
    },
    {
      shortName: 'french',
      longName: 'Frans',
    },
    {
      shortName: 'thai',
      longName: 'Thais',
    },
    {
      shortName: 'chinese',
      longName: 'Chinees',
    },
    {
      shortName: 'mexican',
      longName: 'Mexicaans',
    }
  ]

  return (
    <div className="alterRecipe">
      <div className="alterRecipe__header">
        <h1>Recept aanpassen</h1>
        <div className="alterRecipe__header--buttons">
          <button>Opslaan</button>
          <button>Annuleren</button>
        </div>
      </div>
      <div className="alterRecipe__content">

        <h2>Info van het recept</h2>
        <div className="alterRecipe__content--item">
          <label htmlFor="name">Titel</label>
          <input type="text" defaultValue={name} id="name" />
        </div>
        <div className="alterRecipe__content--item">
          <label htmlFor="quantityPerson">Hoeveelheid personen</label>
          <input type="number" defaultValue={quantityPerson} id="quantityPerson" />
        </div>
        <div className="alterRecipe__content--item">
          <label htmlFor="duration">Bereidingstijd in minuten</label>
          <input type="number" defaultValue={duration} id="duration" />
        </div>
        <div className="alterRecipe__content--item">
          <label htmlFor="labelTypeDish">Soort gerecht</label>
          <select id="labelTypeDish">
            {dishTypes.map((type, index) => {
              return <option key={index} selected={type.shortName === labelTypeDish} value={type.shortName}>{type.longName}</option>
            })}
          </select>
        </div>
        <div className="alterRecipe__content--item">
          <label htmlFor="kitchen">Afkomst van het gerecht</label>
          <select id="kitchen">
            {kitchenTypes.map((type, index) => {
              return <option key={index} selected={type.shortName === kitchen} value={type.shortName}>{type.longName}</option>
            })}
          </select>
        </div>
        <div className="alterRecipe__content--item">
          Is het een gezond gerecht?
          <input type="radio" checked={healthy} id="healthy" value={true} />
          Ja
          <input type="radio" checked={!healthy} id="healthy" value={false} />
          Nee
        </div>
        <div className="alterRecipe__content--item">
          <label htmlFor="image">Afbeelding aanpassen</label>
          <img src={image} alt="" width="200"/>
          <input type="text" defaultValue={image} id="image" />
        </div>

        <h2>Ingrediënten wijzigen, verwijderen of toevoegen</h2>
      </div>
    </div>
  );
};

export default AlterRecipe;
