import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPen } from "@fortawesome/free-solid-svg-icons";

import { AlterRecipe, Modal, RecipeOverviewHeader } from 'config/C4';

const convertQuantityType = (quantityType) => {
  switch (quantityType) {
    case "piece":
      return "";
    case "gram":
      return "gram";
    case "teaspoon":
      return "theelepel";
    default:
      return null;
  }
};

const generateListItemString = ({ name, quantity, quantityType }) => {
  const quantityTypeConverted = convertQuantityType(quantityType);
  if (quantityTypeConverted) {
    return `${quantity} ${quantityTypeConverted} ${name}`;
  }

  return `${quantity} ${name}`;
};



const RecipeOverview = () => {
  const [recipe, setRecipe] = useState({
    name: "Pasta met gehakt, groenten en tomatensaus",
    quantityPerson: 4,
    duration: 90,
    labelTypeDish: "meat",
    kitchen: "italian",
    healthy: false,
    image:
      "https://static.ah.nl/static/recepten/img_RAM_PRD137625_1224x900_JPG.jpg",
    ingredients: [
      {
        name: "paprika",
        quantity: "1",
        quantityType: "piece",
        ingredientType: "vegetable",
      },
      {
        name: "courgette",
        quantity: "1",
        quantityType: "piece",
        ingredientType: "vegetable",
      },
      {
        name: "ui",
        quantity: "1",
        quantityType: "piece",
        ingredientType: "vegetable",
      },
      {
        name: "wortels",
        quantity: "3",
        quantityType: "piece",
        ingredientType: "vegetable",
      },
      {
        name: "tomatenpuree",
        quantity: "70",
        quantityType: "gram",
        ingredientType: "sauce",
      },
      {
        name: "tomatenblokjes",
        quantity: "400",
        quantityType: "gram",
        ingredientType: "sauce",
      },
      {
        name: "pasta",
        quantity: "500",
        quantityType: "gram",
        ingredientType: "doughware",
      },
      {
        name: "geraspte kaas",
        quantity: "200",
        quantityType: "gram",
        ingredientType: "dairy",
      },
      {
        name: "oregano",
        quantity: "1",
        quantityType: "teaspoon",
        ingredientType: "spices",
      },
      {
        name: "gehakt",
        quantity: "500",
        quantityType: "gram",
        ingredientType: "meat",
      },
    ],
    preperationSteps: [
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Viverra justo nec ultrices dui sapien eget mi proin. Proin gravida hendrerit lectus a. Mi quis hendrerit dolor magna eget est lorem.",
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Viverra orci sagittis eu volutpat odio facilisis mauris sit. Odio facilisis mauris sit amet massa vitae.",
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ac tortor dignissim convallis aenean et tortor.",
    ],
  });

  const {
    name,
    quantityPerson,
    duration,
    labelTypeDish,
    kitchen,
    healthy,
    image,
    ingredients,
    preperationSteps,
  } = recipe;

  const [listItems, setListItems] = useState([]);
  const [ modal, openModal ] = useState(false);

  // all ingredients are added to shoppinglist by default
  useEffect(() => {
    const listOfIngredients = [];
    ingredients.map((item) => {
      listOfIngredients.push(item);
    });
    setListItems(listOfIngredients);
  }, [ingredients, setListItems]);

  // function to add or remove item from shoppinglist
  const changeListItems = (e) => {
    const { name } = e.target;
    const itemInList = listItems.some((listItem) => listItem.name === name);
    if (itemInList) {
      const index = listItems.findIndex((listItem) => listItem.name === name);
      const updatedListItems = listItems;
      updatedListItems.splice(index, 1);
      setListItems(updatedListItems);
    } else {
      const updatedListItems = listItems;
      const itemToAdd = ingredients.filter(
        (ingredient) => ingredient.name === name
      );
      updatedListItems.push(itemToAdd[0]);
      setListItems(updatedListItems);
    }
  };

  // function to check if checkbox is checked
  const itemIsInList = (item) => {
    if (listItems.length > 0) {
      const itemInList = listItems.filter(
        (listItem) => listItem.name === item.name
      );
      return itemInList.length > 0;
    }
  };

  return (
    <div className="recipeOverview">
      <RecipeOverviewHeader item={recipe} />
      <section className="recipeOverview__content">
        <div className="recipeOverview__content--ingredients">
          <h1 className="sectionTitle">IngrediÃ«nten</h1>
          <ul>
            {ingredients.map((item, i) => (
              <li key={i}>
                {/* <input
                  type="checkbox"
                  name={item.name}
                  id={generateListItemString(item)}
                  onChange={changeListItems}
                  checked={itemIsInList(item)}
                /> */}
                
                <span className="quantity">
                    {item.quantity} {convertQuantityType(item.quantityType)}
                  </span>
                  <span className="name">&nbsp;{item.name}</span>
              </li>
            ))}
          </ul>

          <button>Toevoegen aan boodschappenlijstje</button>
        </div>
        <div className="recipeOverview__content--preperation-steps">
          <h1>Let's get cooking</h1>
          {preperationSteps.map((item, i) => (
            <div key={i} className="step">
              <div className="number">
                <span>{i + 1}</span>
              </div>
              <p>{item}</p>
            </div>
          ))}
        </div>
      </section>
      <button className="alterRecipe" onClick={() => openModal(!modal)}>
        <FontAwesomeIcon icon={faPen} />
      </button>

      {modal && (
        <Modal>
          <AlterRecipe recipe={recipe} />
        </Modal>
      )}
    </div>
  );
};

export default RecipeOverview;
