import React, { useEffect, useState } from "react";

const convertQuantity = (quantity) => {
  switch (quantity) {
    case "piece":
      return "";
    case "gram":
      return "gram";
    case "teaspoon":
      return "theelepel";
    default:
      return "";
  }
};

const convertLabelTypeDish = (label) => {
  switch (label) {
    case "meat":
      return "vleesgerecht";
    case "fish":
      return "visgerecht";
    case "vegetarian":
      return "vegetarisch gerecht";
    case "vegan":
      return "veganistisch gerecht";
    default:
      return "";
  }
};

const convertKitchen = (kitchen) => {
  switch (kitchen) {
    case "italian":
      return "italiaans";
    default:
      return "";
  }
};

const convertDuration = (duration) => {
  if (duration > 60) {
    const hours = Math.floor(duration / 60);
    const minutes = duration % 60;

    return `${hours} uur en ${minutes} minuten`;
  } else {
    return `${duration} minuten`;
  }
};

const RecipeOverview = () => {
  const [recipe, setRecipe] = useState({
    name: "Pasta met gehakt, groenten en tomatensaus",
    quantityPerson: 4,
    duration: 90,
    labelTypeDish: "meat",
    kitchen: "italian",
    healthy: false,
    image:
      "https://static.ah.nl/static/recepten/img_RAM_PRD137625_1224x900_JPG.jpg",
    ingredients: [
      {
        name: "paprika",
        quantity: "1",
        quantityType: "piece",
        ingredientType: "vegetable",
      },
      {
        name: "courgette",
        quantity: "1",
        quantityType: "piece",
        ingredientType: "vegetable",
      },
      {
        name: "ui",
        quantity: "1",
        quantityType: "piece",
        ingredientType: "vegetable",
      },
      {
        name: "wortels",
        quantity: "3",
        quantityType: "piece",
        ingredientType: "vegetable",
      },
      {
        name: "tomatenpuree",
        quantity: "70",
        quantityType: "gram",
        ingredientType: "sauce",
      },
      {
        name: "tomatenblokjes",
        quantity: "400",
        quantityType: "gram",
        ingredientType: "sauce",
      },
      {
        name: "pasta",
        quantity: "500",
        quantityType: "gram",
        ingredientType: "doughware",
      },
      {
        name: "geraspte kaas",
        quantity: "200",
        quantityType: "gram",
        ingredientType: "dairy",
      },
      {
        name: "oregano",
        quantity: "1",
        quantityType: "teaspoon",
        ingredientType: "spices",
      },
      {
        name: "gehakt",
        quantity: "500",
        quantityType: "gram",
        ingredientType: "meat",
      },
    ],
    preperationSteps: [
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Viverra justo nec ultrices dui sapien eget mi proin. Proin gravida hendrerit lectus a. Mi quis hendrerit dolor magna eget est lorem.",
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Viverra orci sagittis eu volutpat odio facilisis mauris sit. Odio facilisis mauris sit amet massa vitae.",
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ac tortor dignissim convallis aenean et tortor.",
    ],
  });

  const {
    name,
    quantityPerson,
    duration,
    labelTypeDish,
    kitchen,
    healthy,
    image,
    ingredients,
    preperationSteps,
  } = recipe;

  const [ listItems, setListItems ] = useState();

  const changeListItems = (e) => {
    console.log(e.target.value);
    // const itemInList = listItems.filter(listItem => listItem.name === item.name);
    // if (itemInList) {
    //   const index = listItems.findIndex(listItem => listItem.name === item.name);
    //   const updatedListItems = listItems;
    //   updatedListItems.splice(index, 1);
    //   setListItems(updatedListItems);
    //   console.log(listItems);
    // } else {
    //   const updatedListItems = listItems;
    //   updatedListItems.push(item);
    //   setListItems(updatedListItems);
    //   console.log(listItems);
    // }
  }

  return (
    <div className="recipeOverview">
      <header className="recipeOverview__header">
        <div className="recipeOverview__header--text">
          <h1>{name}</h1>
          <div className="duration">{convertDuration(duration)}</div>
          <div className="quantityPerson">{quantityPerson} personen</div>
          <div className="labels">
            <div className="label dish-type">
              {convertLabelTypeDish(labelTypeDish)}
            </div>
            <div className="label kitchen">{convertKitchen(kitchen)}</div>
            {healthy && <div className="label healthy">gezond gerecht</div>}
          </div>
        </div>
        <div className="recipeOverview__header--image">
          <img src={image} alt={name} />
        </div>
      </header>
      <section className="recipeOverview__content">
        <div className="recipeOverview__content--ingredients">
          <h1 className="sectionTitle">IngrediÃ«nten</h1>
          <ul>
            {ingredients.map((item, i) => (
              <li key={i}>
                <input type="checkbox" name={`${item.quantity} ${convertQuantity(item.quantityType)} ${item.name}`} onChange={changeListItems} checked/>
                <label></label>
                <span className="quantity">
                  {item.quantity} {convertQuantity(item.quantityType)}
                </span>
                <span className="name">{item.name}</span>
              </li>
            ))}
          </ul>
        </div>
        <div className="recipeOverview__content--preperation-steps">
          <h1>Let's get cooking</h1>
          {preperationSteps.map((item, i) => (
            <div key={i} className="step">
              <div className="number">
                <span>{i + 1}</span>
              </div>
              <p>{item}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default RecipeOverview;
